client/src/pages/rfq-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import AppLayout from "@/components/layout/AppLayout";
import RFQForm from "@/components/forms/RFQForm";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { useToast } from "@/hooks/use-toast";
import { RFQ } from "@/types";
import { Plus, Search, Filter, MoreHorizontal, Edit, Eye, FileText, Send } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

export default function RFQManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [bondTypeFilter, setBondTypeFilter] = useState("all");
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [editingRFQ, setEditingRFQ] = useState<RFQ | null>(null);
  const { toast } = useToast();

  const { data: rfqs = [], isLoading } = useQuery<RFQ[]>({
    queryKey: ["/api/rfqs"],
  });

  const submitToNSEMutation = useMutation({
    mutationFn: (id: number) => api.put(`/rfqs/${id}`, { 
      status: "submitted",
      submittedAt: new Date().toISOString(),
      nseRfqId: `NSE-${Date.now()}` // Mock NSE ID
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rfqs"] });
      toast({
        title: "Success",
        description: "RFQ submitted to NSE successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.response?.data?.message || "Failed to submit RFQ",
        variant: "destructive",
      });
    },
  });

  const filteredRFQs = rfqs.filter((rfq) => {
    const matchesSearch = rfq.rfqNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         rfq.bondName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (rfq.nseRfqId || "").toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || rfq.status === statusFilter;
    const matchesBondType = bondTypeFilter === "all" || rfq.bondType === bondTypeFilter;
    return matchesSearch && matchesStatus && matchesBondType;
  });

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      pending: { variant: "secondary" as const, label: "Pending", className: "" },
      submitted: { variant: "default" as const, label: "Submitted", className: "bg-blue-100 text-blue-800" },
      executed: { variant: "default" as const, label: "Executed", className: "bg-green-100 text-green-800" },
      cancelled: { variant: "destructive" as const, label: "Cancelled", className: "" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;
    return (
      <Badge variant={config.variant} className={config.className}>
        {config.label}
      </Badge>
    );
  };

  const getBondTypeBadge = (bondType: string) => {
    const typeConfig = {
      government: { variant: "outline" as const, label: "Government" },
      corporate: { variant: "outline" as const, label: "Corporate" },
      municipal: { variant: "outline" as const, label: "Municipal" },
    };

    const config = typeConfig[bondType as keyof typeof typeConfig] || typeConfig.government;
    return (
      <Badge variant={config.variant}>
        {config.label}
      </Badge>
    );
  };

  const handleSubmitToNSE = (rfqId: number) => {
    if (window.confirm("Are you sure you want to submit this RFQ to NSE?")) {
      submitToNSEMutation.mutate(rfqId);
    }
  };

  const totalValue = filteredRFQs.reduce((sum, rfq) => {
    return sum + (parseFloat(rfq.faceValue) * rfq.quantity);
  }, 0);

  return (
    <AppLayout>
      <div className="space-y-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold text-foreground">RFQ Management</h1>
            <p className="text-muted-foreground">Manage Request for Quotes and NSE submissions</p>
          </div>
          <Dialog open={isCreateModalOpen} onOpenChange={setIsCreateModalOpen}>
            <DialogTrigger asChild>
              <Button className="gradient-bg">
                <Plus className="mr-2 h-4 w-4" />
                Create New RFQ
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New RFQ</DialogTitle>
              </DialogHeader>
              <RFQForm onSuccess={() => setIsCreateModalOpen(false)} />
            </DialogContent>
          </Dialog>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total RFQs</p>
                  <p className="text-2xl font-bold text-foreground">{rfqs.length}</p>
                </div>
                <FileText className="h-8 w-8 text-primary" />
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Pending</p>
                  <p className="text-2xl font-bold text-foreground">
                    {rfqs.filter(r => r.status === "pending").length}
                  </p>
                </div>
                <div className="w-8 h-8 bg-yellow-100 rounded-lg flex items-center justify-center">
                  <div className="w-3 h-3 bg-yellow-500 rounded-full" />
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Executed</p>
                  <p className="text-2xl font-bold text-foreground">
                    {rfqs.filter(r => r.status === "executed").length}
                  </p>
                </div>
                <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full" />
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Value</p>
                  <p className="text-2xl font-bold text-foreground">
                    â‚¹{(totalValue / 10000000).toFixed(1)}Cr
                  </p>
                </div>
                <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                  <div className="w-3 h-3 bg-blue-500 rounded-full" />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <Card>
          <CardContent className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search RFQs..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="submitted">Submitted</SelectItem>
                  <SelectItem value="executed">Executed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
              <Select value={bondTypeFilter} onValueChange={setBondTypeFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Filter by bond type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Bond Types</SelectItem>
                  <SelectItem value="government">Government</SelectItem>
                  <SelectItem value="corporate">Corporate</SelectItem>
                  <SelectItem value="municipal">Municipal</SelectItem>
                </SelectContent>
              </Select>
              <Button variant="outline">
                <Filter className="mr-2 h-4 w-4" />
                More Filters
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* RFQs Table */}
        <Card>
          <CardHeader>
            <CardTitle>RFQs ({filteredRFQs.length})</CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="text-center py-8">Loading RFQs...</div>
            ) : filteredRFQs.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No RFQs found
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>RFQ Number</TableHead>
                    <TableHead>Bond Name</TableHead>
                    <TableHead>Bond Type</TableHead>
                    <TableHead>Quantity</TableHead>
                    <TableHead>Face Value</TableHead>
                    <TableHead>Total Value</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>NSE ID</TableHead>
                    <TableHead>Created</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredRFQs.map((rfq) => (
                    <TableRow key={rfq.id}>
                      <TableCell className="font-medium">{rfq.rfqNumber}</TableCell>
                      <TableCell>{rfq.bondName}</TableCell>
                      <TableCell>{getBondTypeBadge(rfq.bondType)}</TableCell>
                      <TableCell>{rfq.quantity.toLocaleString()}</TableCell>
                      <TableCell>â‚¹{parseFloat(rfq.faceValue).toLocaleString()}</TableCell>
                      <TableCell>
                        â‚¹{(parseFloat(rfq.faceValue) * rfq.quantity).toLocaleString()}
                      </TableCell>
                      <TableCell>{getStatusBadge(rfq.status)}</TableCell>
                      <TableCell className="font-mono text-sm">
                        {rfq.nseRfqId || "-"}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {formatDistanceToNow(new Date(rfq.createdAt), { addSuffix: true })}
                      </TableCell>
                      <TableCell>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm">
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => setEditingRFQ(rfq)}>
                              <Edit className="mr-2 h-4 w-4" />
                              Edit
                            </DropdownMenuItem>
                            <DropdownMenuItem>
                              <Eye className="mr-2 h-4 w-4" />
                              View Details
                            </DropdownMenuItem>
                            {rfq.status === "pending" && (
                              <DropdownMenuItem 
                                onClick={() => handleSubmitToNSE(rfq.id)}
                                className="text-primary"
                              >
                                <Send className="mr-2 h-4 w-4" />
                                Submit to NSE
                              </DropdownMenuItem>
                            )}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </CardContent>
        </Card>

        {/* Edit Modal */}
        <Dialog open={!!editingRFQ} onOpenChange={() => setEditingRFQ(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Edit RFQ</DialogTitle>
            </DialogHeader>
            {editingRFQ && (
              <RFQForm 
                rfq={editingRFQ} 
                onSuccess={() => setEditingRFQ(null)} 
              />
            )}
          </DialogContent>
        </Dialog>
      </div>
    </AppLayout>
  );
}

client/src/pages/session-analytics.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import AppLayout from "@/components/layout/AppLayout";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Monitor, Smartphone, Tablet, Clock, Eye, MousePointer, ChevronDown, ChevronRight, User, Calendar, Timer, LogIn, LogOut } from "lucide-react";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { format } from "date-fns";

interface SessionData {
  id: number;
  userId: number;
  userName: string;
  userEmail: string;
  startTime: string;
  endTime: string | null;
  duration: number | null;
  totalPages: number;
  browserName: string;
  deviceType: string;
  endReason: string | null;
  pageViews: Array<{
    pagePath: string;
    pageTitle: string;
    entryTime: string;
    exitTime: string | null;
    duration: number | null;
    scrollDepth: number;
    interactions: number;
  }>;
}

export default function SessionAnalytics() {
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [selectedUserId, setSelectedUserId] = useState("");
  const [expandedSessions, setExpandedSessions] = useState<Record<number, boolean>>({});

  const { data: sessions = [], isLoading, refetch } = useQuery({
    queryKey: ['/api/session-analytics', startDate, endDate, selectedUserId],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (startDate) params.append('startDate', startDate);
      if (endDate) params.append('endDate', endDate);
      if (selectedUserId) params.append('userId', selectedUserId);

      const response = await fetch(`/api/session-analytics?${params.toString()}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (!response.ok) throw new Error('Failed to fetch session analytics');
      return await response.json() as SessionData[];
    },
    staleTime: 0, // Always consider data stale for session analytics
    refetchOnWindowFocus: true,
  });

  // Auto-expand the first session on load
  if (sessions.length > 0 && Object.keys(expandedSessions).length === 0) {
    setExpandedSessions({ [sessions[0].id]: true });
  }

  const formatDuration = (seconds: number | null) => {
    if (!seconds) return "N/A";
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}m ${secs}s`;
  };

  const getDeviceIcon = (deviceType: string) => {
    switch (deviceType.toLowerCase()) {
      case 'mobile': return <Smartphone className="h-4 w-4" />;
      case 'tablet': return <Tablet className="h-4 w-4" />;
      default: return <Monitor className="h-4 w-4" />;
    }
  };

  const getEndReasonColor = (reason: string | null) => {
    switch (reason) {
      case 'logout': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'timeout': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'browser_close': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200';
    }
  };

  const toggleSession = (sessionId: number) => {
    setExpandedSessions(prev => ({
      ...prev,
      [sessionId]: !prev[sessionId]
    }));
  };

  return (
    <AppLayout>
      <div className="p-6 space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Session Analytics</h1>
          <p className="text-muted-foreground mt-2">
            Detailed session tracking with page browsing history and engagement metrics
          </p>
        </div>

        {/* Filters */}
        <Card>
          <CardHeader>
            <CardTitle>Filters</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4 items-end">
              <div className="grid gap-2">
                <label className="text-sm font-medium">Start Date</label>
                <Input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                />
              </div>
              <div className="grid gap-2">
                <label className="text-sm font-medium">End Date</label>
                <Input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                />
              </div>
              <div className="grid gap-2">
                <label className="text-sm font-medium">User ID</label>
                <Input
                  type="number"
                  placeholder="Filter by user ID"
                  value={selectedUserId}
                  onChange={(e) => setSelectedUserId(e.target.value)}
                />
              </div>
              <div className="grid gap-2">
                <label className="text-sm font-medium">Refresh Data</label>
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={async () => {
                    console.log('ðŸ”„ Manual refresh triggered');
                    await refetch();
                    console.log('âœ… Manual refresh completed');
                  }}
                  disabled={isLoading}
                >
                  {isLoading ? 'Refreshing...' : 'Refresh Sessions'}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Session List with Toggle View */}
        <div className="space-y-4">
          {isLoading ? (
            <Card>
              <CardContent className="py-8">
                <div className="text-center">Loading sessions...</div>
              </CardContent>
            </Card>
          ) : sessions.length === 0 ? (
            <Card>
              <CardContent className="py-8">
                <div className="text-center text-muted-foreground">No sessions found</div>
              </CardContent>
            </Card>
          ) : (
            sessions.map((session) => (
              <Card key={session.id} className="overflow-hidden">
                <Collapsible 
                  open={expandedSessions[session.id]} 
                  onOpenChange={() => toggleSession(session.id)}
                >
                  <CollapsibleTrigger className="w-full">
                    <CardHeader className="hover:bg-muted/50 transition-colors">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-4">
                          <div className="flex items-center gap-2">
                            {expandedSessions[session.id] ? 
                              <ChevronDown className="h-4 w-4" /> : 
                              <ChevronRight className="h-4 w-4" />
                            }
                            <User className="h-5 w-5 text-blue-600" />
                          </div>

                          <div className="text-left">
                            <div className="font-semibold">{session.userName}</div>
                            <div className="text-sm text-muted-foreground">{session.userEmail}</div>
                          </div>
                        </div>

                        <div className="flex items-center gap-6 text-sm">
                          {/* Login Time */}
                          <div className="flex items-center gap-2">
                            <LogIn className="h-4 w-4 text-green-600" />
                            <div className="text-right">
                              <div className="font-medium">Login</div>
                              <div className="text-muted-foreground">
                                {format(new Date(session.startTime.replace('Z', '')), "MMM dd, HH:mm")} IST
                              </div>
                            </div>
                          </div>

                          {/* Logout Time */}
                          <div className="flex items-center gap-2">
                            <LogOut className="h-4 w-4 text-red-600" />
                            <div className="text-right">
                              <div className="font-medium">Logout</div>
                              <div className="text-muted-foreground">
                                {session.endTime ? 
                                  format(new Date(session.endTime.replace('Z', '')), "MMM dd, HH:mm") + " IST" :
                                  "Active"
                                }
                              </div>
                            </div>
                          </div>

                          {/* Duration */}
                          <div className="flex items-center gap-2">
                            <Timer className="h-4 w-4 text-purple-600" />
                            <div className="text-right">
                              <div className="font-medium">Duration</div>
                              <div className="text-muted-foreground">
                                {formatDuration(session.duration)}
                              </div>
                            </div>
                          </div>

                          {/* Device & Browser */}
                          <div className="flex items-center gap-2">
                            {getDeviceIcon(session.deviceType)}
                            <div className="text-right">
                              <div className="font-medium">Device</div>
                              <div className="text-muted-foreground">
                                {session.deviceType} - {session.browserName}
                              </div>
                            </div>
                          </div>

                          {/* Pages Count */}
                          <div className="flex items-center gap-2">
                            <Eye className="h-4 w-4 text-orange-600" />
                            <div className="text-right">
                              <div className="font-medium">Pages</div>
                              <div className="text-muted-foreground">
                                {session.totalPages} visited
                              </div>
                            </div>
                          </div>

                          {/* End Reason */}
                          {session.endReason && (
                            <Badge className={getEndReasonColor(session.endReason)}>
                              {session.endReason === 'logout' ? 'Manual Logout' :
                               session.endReason === 'timeout' ? 'Auto Timeout' :
                               session.endReason === 'browser_close' ? 'Browser Close' :
                               session.endReason}
                            </Badge>
                          )}
                        </div>
                      </div>
                    </CardHeader>
                  </CollapsibleTrigger>

                  <CollapsibleContent>
                    <CardContent>
                      <div className="space-y-4">
                        <div className="border-t pt-4">
                          <h4 className="font-semibold mb-3 flex items-center gap-2">
                            <Calendar className="h-4 w-4" />
                            Page Navigation History
                          </h4>

                          {session.pageViews && session.pageViews.length > 0 ? (
                            <div className="rounded-md border">
                              <Table>
                                <TableHeader>
                                  <TableRow>
                                    <TableHead>Page</TableHead>
                                    <TableHead>Entry Time</TableHead>
                                    <TableHead>Exit Time</TableHead>
                                    <TableHead>Duration</TableHead>
                                    <TableHead>Scroll %</TableHead>
                                    <TableHead>Interactions</TableHead>
                                  </TableRow>
                                </TableHeader>
                                <TableBody>
                                  {session.pageViews.map((pageView, index) => (
                                    <TableRow key={index}>
                                      <TableCell>
                                        <div>
                                          <div className="font-medium">{pageView.pageTitle}</div>
                                          <div className="text-sm text-muted-foreground">{pageView.pagePath}</div>
                                        </div>
                                      </TableCell>
                                      <TableCell className="text-sm">
                                        {format(new Date(pageView.entryTime.replace('Z', '')), "HH:mm:ss")} IST
                                      </TableCell>
                                      <TableCell className="text-sm">
                                        {pageView.exitTime ? 
                                          format(new Date(pageView.exitTime.replace('Z', '')), "HH:mm:ss") + " IST" :
                                          "Active"
                                        }
                                      </TableCell>
                                      <TableCell>
                                        <div className="flex items-center gap-1">
                                          <Clock className="h-3 w-3" />
                                          {formatDuration(pageView.duration)}
                                        </div>
                                      </TableCell>
                                      <TableCell>
                                        <div className="flex items-center gap-1">
                                          <Eye className="h-3 w-3" />
                                          {pageView.scrollDepth}%
                                        </div>
                                      </TableCell>
                                      <TableCell>
                                        <div className="flex items-center gap-1">
                                          <MousePointer className="h-3 w-3" />
                                          {pageView.interactions}
                                        </div>
                                      </TableCell>
                                    </TableRow>
                                  ))}
                                </TableBody>
                              </Table>
                            </div>
                          ) : (
                            <div className="text-center py-8 text-muted-foreground">
                              No page views recorded for this session
                            </div>
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </CollapsibleContent>
                </Collapsible>
              </Card>
            ))
          )}
        </div>
      </div>
    </AppLayout>
  );
}

client/src/pages/session-test.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import AppLayout from "@/components/layout/AppLayout";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Timer, LogOut, X, Eye, EyeOff } from "lucide-react";

export default function SessionTest() {
  const { user, logout } = useAuth();
  const [sessionInfo, setSessionInfo] = useState<any>(null);
  const [timeLeft, setTimeLeft] = useState<number>(15 * 60); // 15 minutes in seconds
  const [isTabHidden, setIsTabHidden] = useState(false);

  useEffect(() => {
    // Get current session info
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setSessionInfo({
          userId: payload.userId,
          email: payload.email,
          sessionToken: payload.sessionToken,
          exp: new Date(payload.exp * 1000).toLocaleString()
        });
      } catch (e) {
        console.log('Could not parse token');
      }
    }

    // Update countdown timer
    const interval = setInterval(() => {
      setTimeLeft(prev => Math.max(0, prev - 1));
    }, 1000);

    // Track visibility changes
    const handleVisibilityChange = () => {
      setIsTabHidden(document.visibilityState === 'hidden');
    };
    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      clearInterval(interval);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const testManualLogout = () => {
    console.log('ðŸ§ª Testing Manual Logout...');
    logout();
  };

  const testBrowserClose = () => {
    console.log('ðŸ§ª Testing Browser Close Detection...');

    // Test the actual beforeunload handler
    const beforeUnloadEvent = new Event('beforeunload', { cancelable: true });
    window.dispatchEvent(beforeUnloadEvent);

    // Wait a moment then check if session flags were set
    setTimeout(() => {
      const sessionEndSent = sessionStorage.getItem('sessionEndSent');
      const sessionEndTime = sessionStorage.getItem('sessionEndTime');

      if (sessionEndSent && sessionEndTime) {
        console.log('âœ… Tab close detection working - session end flags set');
        console.log('ðŸ“¡ SessionEndSent:', sessionEndSent);
        console.log('ðŸ“¡ SessionEndTime:', sessionEndTime);

        // Simulate what happens when you open a new tab soon after (refresh scenario)
        setTimeout(() => {
          const timeDiff = Date.now() - parseInt(sessionEndTime);
          if (timeDiff < 3000) {
            console.log('âœ… Quick reload detected - this would be ignored');
            alert('Test successful! Tab close detection is working. Check console for details.');
          }
        }, 1000);
      } else {
        console.log('âŒ Tab close detection not working - no session flags set');
        alert('Test failed! Tab close detection not working properly.');
      }
    }, 500);
  };

  const testTabHide = () => {
    console.log('ðŸ§ª Testing Tab Hide (switch to another tab for 15+ minutes)');
    alert('Switch to another tab for 15+ minutes to test tab hide timeout. Check console for logs.');
  };

  const resetTimer = () => {
    console.log('ðŸ”„ Simulating user activity to reset timer');
    document.dispatchEvent(new Event('mousemove'));
    setTimeLeft(15 * 60);
  };

  return (
    <AppLayout>
      <div className="p-6 space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Session Logout Testing</h1>
          <p className="text-muted-foreground mt-2">
            Test all logout scenarios and session management
          </p>
        </div>

        {/* Current Session Info */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Timer className="h-5 w-5" />
              Current Session Info
            </CardTitle>
          </CardHeader>
          <CardContent>
            {sessionInfo ? (
              <div className="space-y-2">
                <p><strong>User ID:</strong> {sessionInfo.userId}</p>
                <p><strong>Email:</strong> {sessionInfo.email}</p>
                <p><strong>Session Token:</strong> <code className="text-sm bg-muted px-2 py-1 rounded">{sessionInfo.sessionToken}</code></p>
                <p><strong>Token Expires:</strong> {sessionInfo.exp}</p>
                <div className="flex items-center gap-2">
                  <strong>Tab Status:</strong>
                  {isTabHidden ? (
                    <Badge variant="secondary" className="flex items-center gap-1">
                      <EyeOff className="h-3 w-3" />
                      Hidden
                    </Badge>
                  ) : (
                    <Badge variant="default" className="flex items-center gap-1">
                      <Eye className="h-3 w-3" />
                      Visible
                    </Badge>
                  )}
                </div>
              </div>
            ) : (
              <p>No session info available</p>
            )}
          </CardContent>
        </Card>

        {/* Inactivity Timer */}
        <Card>
          <CardHeader>
            <CardTitle>Inactivity Timer</CardTitle>
            <CardDescription>
              Time until automatic logout due to inactivity
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-between">
              <div>
                <div className="text-2xl font-mono">{formatTime(timeLeft)}</div>
                <p className="text-sm text-muted-foreground">
                  {timeLeft === 0 ? 'Session should timeout now!' : 'Time remaining'}
                </p>
              </div>
              <Button onClick={resetTimer} variant="outline">
                Reset Timer (Simulate Activity)
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Logout Tests */}
        <Card>
          <CardHeader>
            <CardTitle>Logout Scenario Tests</CardTitle>
            <CardDescription>
              Test different logout mechanisms and verify proper session ending
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Button 
                onClick={testManualLogout}
                className="flex items-center gap-2"
              >
                <LogOut className="h-4 w-4" />
                Test Manual Logout
              </Button>

              <Button 
                onClick={testBrowserClose}
                variant="outline"
                className="flex items-center gap-2"
              >
                <X className="h-4 w-4" />
                Test Browser Close
              </Button>

              <Button 
                onClick={testTabHide}
                variant="outline"
                className="flex items-center gap-2"
              >
                <EyeOff className="h-4 w-4" />
                Test Tab Hide Timeout
              </Button>

              <Button 
                onClick={() => window.location.reload()}
                variant="secondary"
                className="flex items-center gap-2"
              >
                ðŸ”„ Test Page Refresh (No New Session)
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Instructions */}
        <Card>
          <CardHeader>
            <CardTitle>Test Instructions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <h4 className="font-semibold">1. Manual Logout Test</h4>
                <p className="text-sm text-muted-foreground">
                  Click "Test Manual Logout" and verify session ends with reason 'logout' in Session Analytics
                </p>
              </div>

              <div>
                <h4 className="font-semibold">2. Inactivity Timeout Test</h4>
                <p className="text-sm text-muted-foreground">
                  Don't interact with the page for 15 minutes. Watch the timer count down and verify automatic logout.
                </p>
              </div>

              <div>
                <h4 className="font-semibold">3. Browser Close Test</h4>
                <p className="text-sm text-muted-foreground">
                  Click "Test Browser Close" to simulate closing the browser. Check Session Analytics after reopening.
                </p>
              </div>

              <div>
                <h4 className="font-semibold">4. Tab Hide Test</h4>
                <p className="text-sm text-muted-foreground">
                  Switch to another tab for 15+ minutes. Return to verify session ended with 'browser_close' reason.
                </p>
              </div>

              <div>
                <h4 className="font-semibold">5. Page Refresh Test</h4>
                <p className="text-sm text-muted-foreground">
                  Refresh the page multiple times. Verify the same session is maintained (no duplicates created).
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </AppLayout>
  );
}

client/src/pages/support-tickets.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import AppLayout from "@/components/layout/AppLayout";
import SupportTicketForm from "@/components/forms/SupportTicketForm";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { useToast } from "@/hooks/use-toast";
import { SupportTicket } from "@/types";
import { Plus, Search, Filter, MoreHorizontal, Edit, Eye, CheckCircle, Clock } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

export default function SupportTickets() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [priorityFilter, setPriorityFilter] = useState("all");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [editingTicket, setEditingTicket] = useState<SupportTicket | null>(null);
  const { toast } = useToast();

  const { data: tickets = [], isLoading } = useQuery<SupportTicket[]>({
    queryKey: ["/api/support-tickets"],
  });

  const updateStatusMutation = useMutation({
    mutationFn: ({ id, status, resolution }: { id: number; status: string; resolution?: string }) =>
      api.put(`/support-tickets/${id}`, { 
        status, 
        resolution,
        resolvedAt: status === "resolved" ? new Date().toISOString() : undefined
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/support-tickets"] });
      toast({
        title: "Success",
        description: "Ticket status updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.response?.data?.message || "Failed to update ticket",
        variant: "destructive",
      });
    },
  });

  const filteredTickets = tickets.filter((ticket) => {
    const matchesSearch = ticket.ticketNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         ticket.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         ticket.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || ticket.status === statusFilter;
    const matchesPriority = priorityFilter === "all" || ticket.priority === priorityFilter;
    const matchesCategory = categoryFilter === "all" || ticket.category === categoryFilter;
    return matchesSearch && matchesStatus && matchesPriority && matchesCategory;
  });

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      open: { variant: "destructive" as const, label: "Open", className: "" },
      in_progress: { variant: "secondary" as const, label: "In Progress", className: "" },
      resolved: { variant: "default" as const, label: "Resolved", className: "bg-green-100 text-green-800" },
      closed: { variant: "outline" as const, label: "Closed", className: "" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.open;
    return (
      <Badge variant={config.variant} className={config.className}>
        {config.label}
      </Badge>
    );
  };

  const getPriorityBadge = (priority: string) => {
    const priorityConfig = {
      low: { variant: "outline" as const, label: "Low", className: "" },
      medium: { variant: "secondary" as const, label: "Medium", className: "" },
      high: { variant: "default" as const, label: "High", className: "bg-orange-100 text-orange-800" },
      urgent: { variant: "destructive" as const, label: "Urgent", className: "" },
    };

    const config = priorityConfig[priority as keyof typeof priorityConfig] || priorityConfig.medium;
    return (
      <Badge variant={config.variant} className={config.className}>
        {config.label}
      </Badge>
    );
  };

  const getCategoryBadge = (category: string) => {
    const categoryLabels = {
      technical: "Technical",
      trading: "Trading",
      kyc: "KYC",
      general: "General",
    };

    return (
      <Badge variant="outline">
        {categoryLabels[category as keyof typeof categoryLabels] || category}
      </Badge>
    );
  };

  const handleStatusUpdate = (ticketId: number, newStatus: string) => {
    let resolution = undefined;
    if (newStatus === "resolved") {
      resolution = prompt("Please enter the resolution details:");
      if (!resolution) return;
    }

    updateStatusMutation.mutate({ id: ticketId, status: newStatus, resolution });
  };

  const openTickets = tickets.filter(t => t.status === "open").length;
  const inProgressTickets = tickets.filter(t => t.status === "in_progress").length;
  const resolvedTickets = tickets.filter(t => t.status === "resolved").length;
  const urgentTickets = tickets.filter(t => t.priority === "urgent").length;

  return (
    <AppLayout>
      <div className="space-y-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold text-foreground">Support Tickets</h1>
            <p className="text-muted-foreground">Manage customer support requests and issues</p>
          </div>
          <Dialog open={isCreateModalOpen} onOpenChange={setIsCreateModalOpen}>
            <DialogTrigger asChild>
              <Button className="gradient-bg">
                <Plus className="mr-2 h-4 w-4" />
                Create New Ticket
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Support Ticket</DialogTitle>
              </DialogHeader>
              <SupportTicketForm onSuccess={() => setIsCreateModalOpen(false)} />
            </DialogContent>
          </Dialog>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Open Tickets</p>
                  <p className="text-2xl font-bold text-foreground">{openTickets}</p>
                </div>
                <div className="w-8 h-8 bg-red-100 rounded-lg flex items-center justify-center">
                  <div className="w-3 h-3 bg-red-500 rounded-full" />
                </div>
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">In Progress</p>
                  <p className="text-2xl font-bold text-foreground">{inProgressTickets}</p>
                </div>
                <Clock className="h-8 w-8 text-yellow-500" />
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Resolved</p>
                  <p className="text-2xl font-bold text-foreground">{resolvedTickets}</p>
                </div>
                <CheckCircle className="h-8 w-8 text-green-500" />
              </div>
            </CardContent>
          </Card>
          <Card className="card-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Urgent</p>
                  <p className="text-2xl font-bold text-foreground">{urgentTickets}</p>
                </div>
                <div className="w-8 h-8 bg-red-100 rounded-lg flex items-center justify-center">
                  <div className="w-3 h-3 bg-red-600 rounded-full animate-pulse" />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <Card>
          <CardContent className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search tickets..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="open">Open</SelectItem>
                  <SelectItem value="in_progress">In Progress</SelectItem>
                  <SelectItem value="resolved">Resolved</SelectItem>
                  <SelectItem value="closed">Closed</SelectItem>
                </SelectContent>
              </Select>
              <Select value={priorityFilter} onValueChange={setPriorityFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Filter by priority" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Priorities</SelectItem>
                  <SelectItem value="low">Low</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                  <SelectItem value="urgent">Urgent</SelectItem>
                </SelectContent>
              </Select>
              <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Filter by category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  <SelectItem value="technical">Technical</SelectItem>
                  <SelectItem value="trading">Trading</SelectItem>
                  <SelectItem value="kyc">KYC</SelectItem>
                  <SelectItem value="general">General</SelectItem>
                </SelectContent>
              </Select>
              <Button variant="outline">
                <Filter className="mr-2 h-4 w-4" />
                More Filters
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Tickets Table */}
        <Card>
          <CardHeader>
            <CardTitle>Support Tickets ({filteredTickets.length})</CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="text-center py-8">Loading tickets...</div>
            ) : filteredTickets.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No tickets found
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Ticket #</TableHead>
                    <TableHead>Subject</TableHead>
                    <TableHead>Category</TableHead>
                    <TableHead>Priority</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Created</TableHead>
                    <TableHead>Last Updated</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredTickets.map((ticket) => (
                    <TableRow key={ticket.id}>
                      <TableCell className="font-medium font-mono">
                        {ticket.ticketNumber}
                      </TableCell>
                      <TableCell>
                        <div>
                          <p className="font-medium">{ticket.subject}</p>
                          <p className="text-sm text-muted-foreground truncate max-w-xs">
                            {ticket.description}
                          </p>
                        </div>
                      </TableCell>
                      <TableCell>{getCategoryBadge(ticket.category)}</TableCell>
                      <TableCell>{getPriorityBadge(ticket.priority)}</TableCell>
                      <TableCell>{getStatusBadge(ticket.status)}</TableCell>
                      <TableCell className="text-muted-foreground">
                        {formatDistanceToNow(new Date(ticket.createdAt), { addSuffix: true })}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {formatDistanceToNow(new Date(ticket.updatedAt), { addSuffix: true })}
                      </TableCell>
                      <TableCell>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm">
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => setEditingTicket(ticket)}>
                              <Edit className="mr-2 h-4 w-4" />
                              Edit
                            </DropdownMenuItem>
                            <DropdownMenuItem>
                              <Eye className="mr-2 h-4 w-4" />
                              View Details
                            </DropdownMenuItem>
                            {ticket.status === "open" && (
                              <DropdownMenuItem 
                                onClick={() => handleStatusUpdate(ticket.id, "in_progress")}
                              >
                                Start Working
                              </DropdownMenuItem>
                            )}
                            {(ticket.status === "open" || ticket.status === "in_progress") && (
                              <DropdownMenuItem 
                                onClick={() => handleStatusUpdate(ticket.id, "resolved")}
                                className="text-green-600"
                              >
                                Mark Resolved
                              </DropdownMenuItem>
                            )}
                            {ticket.status === "resolved" && (
                              <DropdownMenuItem 
                                onClick={() => handleStatusUpdate(ticket.id, "closed")}
                              >
                                Close Ticket
                              </DropdownMenuItem>
                            )}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </CardContent>
        </Card>

        {/* Edit Modal */}
        <Dialog open={!!editingTicket} onOpenChange={() => setEditingTicket(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Edit Support Ticket</DialogTitle>
            </DialogHeader>
            {editingTicket && (
              <SupportTicketForm 
                ticket={editingTicket} 
                onSuccess={() => setEditingTicket(null)} 
              />
            )}
          </DialogContent>
        </Dialog>
      </div>
    </AppLayout>
  );
}

